  `building` set('bd1','bd2','bd3','bd4','yd') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `location` varchar(32) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `summary` text COLLATE utf8mb4_unicode_ci,
  `category` set('academic','activity','display_literature','display_science','video','music','performance','game_watching','foods','purchase') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `thumbimg` varchar(512) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `name_furigana` varchar(128) COLLATE utf8mb4_unicode_ci NOT NULL,
  `groupname_furigana` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

