INSERT INTO `exhibits` (`id`, `name`, `groupname`, `building`, `location`, `summary`, `category`, `thumbimg`, `name_furigana`, `groupname_furigana`) VALUES
(542, '受験案内', '', 'bd1', '201・202', '一年生や担当教員による受験案内を行います。受験生の方はぜひお越しください。', 'academic', '\"1号館\\中学一年A.jpeg\"', 'じゅけんあんない', ''),
(543, '中学１年生展示', '中学１年生', 'bd1', '203・204', '受験生必見のコーナー。夏の林間学校（長野・志賀高原）での作品の展示や、力作ぞろいの自由研究発表を行います。', 'academic', '\"1号館\\中学一年B.jpg\"', 'ちゅうがく1ねんせいてんじ', 'ちゅうがく1ねんせい'),
(544, '羽射', 'バドミントン部', 'bd1', '205', '今年もバトミントン部は射的！素敵な部員が優しく教えます！豪華(？)景品もあります！あなたのハートを狙い撃ち！', 'activity', '\"1号館\\バトミントン部.jpeg\"', 'はねしゃ', 'ばとみんとんぶ'),
(545, 'Labyrinth over the phantom', '実行委員有志', 'bd1', '206', '校内全体を使った謎解きゲーム、、、。張り巡らされた問題をすべて解き、爽快な気持ちで帰ることが出来るか、、、。', 'activity', '\"1号館\\実行委員有志.jpg\"', 'Labyrinth over the phantom', 'じっこういいんゆうし'),
